--  DDL for Procedure WIRELINE_GET_DOUBLE_BUNDLE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PCATALOGUE"."WIRELINE_GET_DOUBLE_BUNDLE" 
(
location varchar2,
Package1 varchar2,
Package2 varchar2
) AS
BEGIN
  delete from temp;
  insert into temp(
    select table1.id,descc,discount,cost FROM
    (select Bundle_Products.BUNDLE_TYPE_CD as id, Sum(products.cost) as cost from Bundle_Products, Products
      where Bundle_Products.Product_Id = Products.Product_Id
      group by Bundle_Products.bundle_type_Cd
      having Bundle_Products.Bundle_Type_Cd in 
      (select distinct(Bundle_Products.BUNDLE_TYPE_CD)
      from Bundle_Products join Package_Filters on Bundle_Products.PRODUCT_ID = Package_Filters.Product_Id
      where filter_type = 'notstate' and
      filter_value1 not like '%'||location||'%' and
      Bundle_Type_CD IN (select distinct (Bundle_Type_cd) 
      from Package_Bundle
      where Package_Id = Package1
      intersect (select distinct (Bundle_Type_cd) 
      from Package_Bundle
      where Package_Id = Package2)))
      ) table1,
      (select Bundle_Types.Bundle_Type_Cd as id, Bundle_Types.Bundle_Desc as descc, Discount.Discount_Id as discount
    from Bundle_Types join Discount on Bundle_Types.Bundle_Type = Discount.Product_Type
    where Bundle_Types.BUNDLE_LEVEL_CD = 'dp' and 
    Bundle_Type_Cd in 
      (select distinct(Bundle_Products.BUNDLE_TYPE_CD)
      from Bundle_Products join Package_Filters on Bundle_Products.PRODUCT_ID = Package_Filters.Product_Id
      where filter_type = 'notstate' and
      filter_value1 not like '%'||location||'%' and
      Bundle_Type_CD IN 
        (select distinct (Bundle_Type_cd) 
        from Package_Bundle
        where Package_Id = Package1
        intersect 
          (select distinct (Bundle_Type_cd) 
          from Package_Bundle
          where Package_Id = Package2)
        )
      )
    ) table2
    where table1.id = table2.id);
    commit;
End;

